var $nav=$(".navbar");var $navbarNav=$(".navbar-nav");var $cover=$("header");var $top=$("#backToTop");var $nextSec=$("#nextSec");var $bottomNav=$("#bottomNav");new WOW().init();$(function(){$(document).scroll(function(){$nav.toggleClass('scrolled',$(this).scrollTop()>($cover.height()-$nav.height()));$bottomNav.toggleClass('scrolled',$(this).scrollTop()>($cover.height()-$nav.height()));$top.toggleClass('scrolled',$(this).scrollTop()>($cover.height()-$nav.height()));$nextSec.toggleClass('scrolled',$(this).scrollTop()>0);});});function topFunction(){document.body.scrollTop=0;document.documentElement.scrollTop=0;}function nextSecFunction(){document.body.scrollTop=($cover.height()-$nav.height());document.documentElement.scrollTop=($cover.height()-$nav.height());}